(function(){tinymce.PluginManager.requireLangPack('carousel');tinymce.create('tinymce.plugins.CarouselPlugin',{init:function(ed,url){ed.addCommand('mceCarousel',function(){var url_uid="./folder.html";var carousel=tinyMCE.activeEditor.dom.select('.carousel');if(carousel&&carousel!=''){var path = tinyMCE.activeEditor.dom.getAttrib(carousel,"id","");if(path=="")url_uid="./folder.html";else url_uid="./folder.html?path="+path;}ed.windowManager.open({title:"\u6587\u4ef6\u6d4f\u89c8",file:url_uid,width:420+ed.getLang('carousel.delta_width',0),height:560+ed.getLang('carousel.delta_height',0),inline:1},{plugin_url:url,type:"carousel"})});ed.addButton('carousel',{title:'Carousel',cmd:'mceCarousel',image:url+'/img/next.gif'})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'Carousel plugin',author:'Some author',authorurl:'http://tinymce.moxiecode.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/example',version:"1.0"}}});tinymce.PluginManager.add('carousel',tinymce.plugins.CarouselPlugin)})();