            $vcard.c("N");
            vcard.first_name && $vcard.c("GIVEN").t(vcard.first_name).up();
            vcard.last_name && $vcard.c("FAMILY").t(vcard.last_name).up();
            vcard.middle_name && $vcard.c("MIDDLE").t(vcard.middle_name).up();
            $vcard.up();
